global.tutorial_text_16 = {string = "Zuerst musst du die auszusendenden Helden ausw\195\164hlen"}
global.tutorial_text_17 = {string = "In der Kategorie Aussenden kannst du das Profilbild der Helden ziehen und ihre Position ver\195\164ndern"}
global.tutorial_text_18 = {string = "Alle Vorkehrungen sind getroffen - dann begeben wir uns endlich zur ersten Schlacht!"}
global.tutorial_text_19 = {string = "Die 2 Positionen in der Vorderreihe m\195\188ssen es mit vielen Angriffen aufnehmen. Bitte setze Helden mit einer starken Verteidigung ein."}
global.tutorial_text_20 = {string = "Ein Held sammelt Energie, wenn er angreift oder Schaden erleidet. Ist der Energiebalken voll, wird seine F\195\164higkeit ausgel\195\182st."}
global.tutorial_text_21 = {string = "Na das hat sich doch gelohnt! Helden kannst du nicht nur beschw\195\182ren, sondern auch noch mit Fragmenten zusammensetzen. Als n\195\164chstes werden wir lernen, wie wir Helden mit Fragmenten zusammensetzen k\195\182nnen."}
global.tutorial_text_22 = {string = "Auf deinen gef\195\164hrlichen Abenteuern kommst du nicht um starke Helden umhin. Jetzt sehen wir uns einmal an, wie wir Helden f\195\182rdern k\195\182nnen"}
global.tutorial_text_23 = {string = "\195\156berfl\195\188ssige Helden kannst du zu Heldenseelen zerlegen. Heldenseelen sind sehr wichtige Materialien, um deine Helden zu verbessern"}
global.tutorial_text_24 = {string = "Bitte w\195\164hle einen zu zerlegenden Helden aus"}
global.tutorial_text_25 = {string = "Nicht schlecht! Jetzt werden wir einen Helden verbessern"}
global.tutorial_text_26 = {string = "Sobald dein Helden-EP voll ist, kannst du hier tippen und deinen Helden verbessern"}
global.tutorial_text_27 = {string = "Sehr gut! Als n\195\164chstes werden wir dem Helden etwas Ausr\195\188stung anlegen"}
global.tutorial_text_28 = {string = "Tippe auf das Waffen-Icon und w\195\164hle Waffen"}
global.tutorial_text_29 = {string = "Sch\195\182n gemacht! Du bist schon wieder st\195\164rker geworden!"}
global.tutorial_text_30 = {string = "Jetzt werden wir uns wieder unserem Abenteuer widmen"}
global.tutorial_text_31 = {string = "Um eine Instanz freizuschalten, brauchst du eine starke Auto-Kampfaufstellung. Als n\195\164chstes werden wir neue Auto-Kampfhelden hinzuf\195\188gen "}
global.tutorial_text_32 = {string = "Fantastisch! Die Auto-Kampftruppe wird mit deinem Spieler-Lv allm\195\164hlich freigeschaltet. Bitte vergiss nicht, stets neue Helden hinzuzuf\195\188gen!"}
global.tutorial_text_33 = {string = "Tippe hier, um deine Auto-Kampfbeute abzuholen. Mit der Zeit erh\195\164ltst du mehr und mehr Kriegsbedeutet! "}
global.tutorial_text_34 = {string = "Du bist jetzt stark genug. Eine unbekannte Welt wartet darauf, von dir erkundet zu werden! Krieger, ich w\195\188nsche dir viel Gl\195\188ck! Lebe wohl!"}
global.tutorial_text_35 = {string = "Der Markt ist bereits freigeschaltet. Seltene Sch\195\164tze, von denen Sie nur tr\195\164umen k\195\182nnen, warten hier, um von Ihnen entdeckt zu werden!"}
global.tutorial_text_36 = {string = "Willkommen beim Gl\195\188cksrad, Krieger! M\195\182chten Sie Ihr Gl\195\188ck versuchen?"}
global.tutorial_text_37 = {string = "Registrieren Sie Ihr Konto, damit Sie sich von verschiedenen Ger\195\164ten anmelden k\195\182nnen"}
global.tutorial_text_38 = {string = "In der Arena k\195\182nnen Sie gegen andere Spieler um Ehre k\195\164mpfen"}
global.tutorial_text_39 = {string = "Bitte stellen Sie Ihre Verteidigungsaufstellung f\195\188r die Arena ein"}
global.tutorial_text_40 = {string = "Der Traumlandturm ist bereits freigeschaltet. Unz\195\164hlige Sch\195\164tze warten auf Sie - und nat\195\188rlich auch die im Hintergrund lauernden Gefahren!"}
global.tutorial_text_41 = {string = "Willkommen in der Taverne! Senden Sie Ihre Helden aus, um Missionen zu erf\195\188llen und warten lediglich auf Ihre Belohnung!"}
global.tutorial_text_42 = {string = "Ab der entsprechenden Anzahl k\195\182nnen Sie mit Fragmenten diesen Helden beschw\195\182ren. Tippen Sie auf das Profilbild des Helden"}
