global.tutorial_text_1 = {string = "Benvenuto nella terra dei guerrieri. Scegli un nome degno di te!"}
global.tutorial_text_2 = {string = "Che nome fantastico! Diamo un'occhiata in giro."}
global.tutorial_text_3 = {string = "Questo \195\168 il cerchio magico, dove puoi evocare gli eroi."}
global.tutorial_text_4 = {string = "Con l'evocazione base puoi ottenere eroi di bassa qualit\195\160. L'evocazione base richiede una pergamena di evocazione base."}
global.tutorial_text_5 = {string = "Okay, proviamo. Per questa volta \195\168 gratis."}
global.tutorial_text_6 = {string = "Con l'evocazione eroica puoi ottenere eroi di alta qualit\195\160 (con 3, 4 e 5 stelle). L'evocazione eroica richiede una pergamena di evocazione eroica."}
global.tutorial_text_7 = {string = "Okay, proviamo. Per questa volta \195\168 gratis."}
global.tutorial_text_8 = {string = "Fantastico! Hai imparato a evocare gli eroi. Benissimo! Ora possiamo dare uno sguardo al portale delle segrete."}
global.tutorial_text_9 = {string = "Varca il portale delle segrete per entrare nell'area di battaglia. Proviamo a combattere!"}
global.tutorial_text_10 = {string = "Prima che la battaglia abbia inizio, dobbiamo impostare gli eroi."}
global.tutorial_text_11 = {string = "Tocca l'avatar dell'eroe che vuoi schierare."}
global.tutorial_text_12 = {string = "Pronto? Via!"}
global.tutorial_text_13 = {string = "Gli eroi schierati combatteranno automaticamente, conquistando un bottino per te."}
global.tutorial_text_14 = {string = "Vedi la barra di avanzamento? Stiamo esplorando questa tappa. La tappa sta per concludersi; prepariamoci all'inizio della battaglia 1."}
global.tutorial_text_15 = {string = "La battaglia \195\168 iniziata! Diamo un'occhiata."}
global.tutorial_text_16 = {string = "Per prima cosa, schiera degli eroi."}
global.tutorial_text_17 = {string = "Puoi trascinare gli avatar degli eroi per cambiarne la posizione."}
global.tutorial_text_18 = {string = "Pronto? Ecco la tua prima battaglia!"}
global.tutorial_text_19 = {string = "Gli eroi che occupano le 2 posizioni davanti sono i primi a dover resistere all'attacco. \195\168 una buona idea schierare in questa posizione gli eroi con una difesa alta."}
global.tutorial_text_20 = {string = "Attaccando e danneggiando gli avversari, gli eroi accumulano energia. Quando la barra dell'energia \195\168 piena, puoi usare le abilit\195\160."}
global.tutorial_text_21 = {string = "Ben fatto! Sai che puoi combinare dei frammenti per creare eroi? Bene! Vediamo come si fa."}
global.tutorial_text_22 = {string = "Hai bisogno di eroi potenti che ti accompagnino nel tuo viaggio all'insegna dell'avventura. Vediamo come fare per rendere pi\195\185 forti gli eroi!"}
global.tutorial_text_23 = {string = "Gli eroi possono essere disassemblati e ridotti a SPIRITO, un elemento importante per potenziare altri eroi."}
global.tutorial_text_24 = {string = "Scegli gli eroi da disassemblare."}
global.tutorial_text_25 = {string = "Benissimo! Ora potenziamo gli eroi."}
global.tutorial_text_26 = {string = "Quando gli eroi hanno punti ESP a sufficienza, tocca qui per potenziarli."}
global.tutorial_text_27 = {string = "Bene! Diamo agli eroi dell'equipaggiamento."}
global.tutorial_text_28 = {string = "Per scegliere un'arma, tocca l'icona corrispondente."}
global.tutorial_text_29 = {string = "Eccellente! Ora sei molto pi\195\185 forte!"}
global.tutorial_text_30 = {string = "Bene, ora possiamo continuare il nostro viaggio."}
global.tutorial_text_31 = {string = "Per sbloccare nuove tappe servono formazioni di combattimento automatico forti. Aggiungiamo qualche altro eroe."}
global.tutorial_text_32 = {string = "Benissimo! L'elenco di combattimento automatico si sblocca salendo di livello. Ricorda di usare i nuovi eroi per tempo."}
global.tutorial_text_33 = {string = "Tocca qui per riscuotere il bottino del combattimento automatico. Pi\195\185 lunga \195\168 la battaglia, pi\195\185 ricca sar\195\160 la ricompensa."}
