-- Command line was: E:\github\dhgametool\scripts\config\strings\it\help.lua 

local help = {}
help[1] = {title = "Funzioni di costruzione", describe = {{title = "Battaglia", content = "Invia in battaglia le squadre di eroi per attivare la battaglia automatica e completare gli eventi di battaglia. Le battaglie fanno ottenere ESP, spirito, oro ed equipaggiamento. Inoltre, \195\168 possibile ottenere ricompense doppie in determinati periodi."}, {title = "Prova dell'eroe", content = "Combatti infinite battaglie senza alcun limite e sconfiggi i nemici di server diversi. L'eroe non recupera HP durante la prova. Ecco perch\195\169 hai bisogno di una buona strategia!"}, {title = "Torre dell'oblio", content = "La Torre dell'oblio \195\168 sotto stretta sorveglianza. Sconfiggendo le guardie, otterrai tonnellate di pietre promozione e potenti oggetti dell'equipaggiamento. Ad alcuni piani puoi vincere addirittura eroi con 5 stelle! Pi\195\185 alto \195\168 il piano, pi\195\185 le guardie sono toste."}, {title = "Arena", content = "Scontrati con altri giocatori nell'arena! I vincitori possono ottenere punti e diamanti gratis. La prova del campione si sblocca ogni venerd\195\172, sabato e domenica (ora del server). Vieni a scontrarti con i giocatori di altri server!"}, {title = "Cerchio dell'evocazione", content = "Il modo pi\195\185 rapido per ottenere nuovi eroi. Raccogli pi\195\185 cuori o pergamene di evocazione per evocare gratuitamente gli eroi. Quando l'energia di evocazione \195\168 piena, ottieni un eroe casuale con 5 stelle."}, {title = "Cerchio della creazione", content = "\195\136 l'unico modo per far avanzare gli eroi. Raccogli il materiale richiesto per incrementare il livello di stelle degli eroi fino a 6 nel Cerchio dell'evocazione. Gli eroi di partenza sacrificati si trasformeranno in spirito e pietre promozione. L'equipaggiamento utilizzato dagli eroi di partenza torner\195\160 nello zaino. Pi\195\185 eroi si hanno a disposizione, maggiori saranno le possibilit\195\160 di avanzare."}, {title = "Altare", content = "Puoi disassemblare gli eroi inutilizzati per ottenere spirito, pietre promozione, polvere magica e pietre anima. L'equipaggiamento, i cristalli e gli artefatti degli eroi disassemblati torneranno nello zaino. Le pietre anima possono essere scambiate con eroi nel negozio dell'altare. Le pietre anima si possono ottenere anche con i pacchetti degli eventi!"}, {title = "Fabbro", content = "Dal fabbro puoi combinare equipaggiamento di basso livello per ottenerne di altro livello. Indossando un equipaggiamento completo, si attivano le statistiche dei set. Attenzione: l'equipaggiamento in uso non viene visualizzato dal fabbro."}, {title = "Casin\195\178", content = "Al casin\195\178 puoi giocare dei gettoni per vincere frammenti di eroi, equipaggiamento, artefatti, oro e spirito. Buona fortuna!"}, {title = "Mercato", content = "Gli oggetti al mercato si aggiornano periodicamente (puoi aggiornarli anche manualmente). Puoi ottenere spirito, eroi, pietre promozione, frammenti di eroi, equipaggiamento, pergamene di evocazione, biglietti arena e gettoni."}, {title = "Albero del profeta", content = "Nell'albero del profeta puoi evocare eroi di una determinata fazione. Usa la gemma del profeta nel portale dell'evocazione per ottenere eroi o frammenti generici con 4 o 5 stelle (puoi ottenere gemme del profeta nelle missioni della taverna o con i pacchetti degli eventi). Ogni evocazione fa ottenere un desiderio del profeta. Quando il desiderio \195\168 al completo, puoi cambiare una volta la fazione dell'eroe. Attenzione: solo gli eroi con 4 e 5 stelle possono essere cambiati (esclusi gli eroi della luce e dell'oscurit\195\160)."}, {title = "Taverna", content = "Nella taverna sono disponibili missioni degli eroi di diversi livelli di stelle che si aggiornano ogni 24 ore. Completando le missioni degli eroi, otterrai grandi ricompense. Con i diamanti puoi cambiare le stelle delle missioni e portarle a temine velocemente. Per avere pi\195\185 missioni, puoi usare pergamene missione (aggiornando, vengono cambiate anche le missioni incomplete). Il combattimento automatico pu\195\178 farti ottenere missioni della taverna."}}}
help[2] = {title = "Dettagli tappa", describe = {{title = "Panoramica", content = "Le mappe della battaglia hanno 4 livelli di difficolt\195\160: Normale, Incubo, Inferno e Morte. Ogni livello di difficolt\195\160 ha determinate tappe. Le difficolt\195\160 vanno sbloccate in sequenza."}, {title = "Sbloccaggio della tappa", content = "Rimanendo nelle nuove tappe per un certo periodo di tempo, si innescano eventi battaglia. Dopo il completamento degli eventi, puoi accedere alla tappa successiva se il livello e la potenza della squadra sono sufficienti. "}, {title = "Sbloccaggio di area e mappa", content = "Una volta completate tutte le tappe di un'area, si sblocca l'area successiva. Dopo aver superato tutte le tappe di un livello di difficolt\195\160, si sblocca il livello di difficolt\195\160 successivo."}, {title = "Evento di battaglia", content = "L'ingresso in una nuova tappa innesca un evento di battaglia. Completando l'evento, si ottengono ricche ricompense."}, {title = "Squadra", content = "Puoi impostare la squadra di eroi nel menu della squadra. Puoi aggiungere ulteriori eroi alla squadra quando sali di livello. All'aumentare della potenza degli eroi, potrai schierare pi\195\185 eroi, incrementando cos\195\172 la potenza della squadra (la potenza degli eroi schierati conta solo per sbloccare le tappe. \195\136 irrilevante ai fini del trofeo)."}, {title = "Battaglia automatica", content = "Le entrate della battaglia automatica aumentano col passare del tempo. La battaglia automatica pu\195\178 durare fino a 8 ore, trascorse le quali non otterrai alcuna ricompensa. Ricordati di ritirare le tue entrate entro 8 ore!"}, {title = "Oggetti ed entrate", content = "Ogni tappa ti far\195\160 guadagnare determinate quantit\195\160 di ESP, oro e spirito. Potrai ottenerli durante la battaglia automatica. All'aumentare della difficolt\195\160 e con l'avanzare delle tappe aumentano anche le entrate. Le entrate aumentano anche all'aumentare del livello VIP."}, {title = "Bottino e trofeo", content = "Puoi controllare quali sono gli oggetti ottenibili a ciascuna tappa consultando le informazioni relative al bottino. Il combattimento automatico consente di guadagnare gli oggetti corrispondenti a ogni tappa. Potrai verificare le ricompense guadagnate nel menu del trofeo."}}}
help[3] = {title = "Battaglia", describe = {{title = "Posizionamento prima della battaglia", content = "Gli eroi in prima linea hanno maggiori probabilit\195\160 di essere attaccati rispetto a quelli in seconda linea. Scegli la posizione degli eroi in base alla loro classe affinch\195\169 l'intera squadra possa resistere pi\195\185 a lungo."}, {title = "Limite della fazione", content = "Gli eroi di diverse fazioni si limitano a vicenda. Forma la squadra in base agli eroi nemici per avere un vantaggio su di loro."}, {title = "Aura della fazione", content = "Schierando 6 eroi della stessa fazione o eroi di 6 fazioni diverse, si attiva una potente aura."}, {title = "Regole della battaglia", content = "Se la durata della battaglia supera i 15 round, la squadra attaccante perde. Se tutti gli eroi nemici vengono uccisi entro il numero previsto di round, la squadra attaccante vince. Si perde la battaglia se tutti gli eroi della squadra vengono uccisi."}, {title = "Attacco", content = "Pi\195\185 \195\168 alto l'attacco di un eroe, maggiori sono i danni che pu\195\178 causare."}, {title = "HP", content = "Pi\195\185 alto \195\168 il livello di HP dell'eroe, pi\195\185 a lungo questi pu\195\178 sopravvivere."}, {title = "Armatura", content = "Pi\195\185 alta \195\168 la resistenza dell'armatura dell'eroe, meno danni questi subir\195\160."}, {title = "Velocit\195\160", content = "La velocit\195\160 determina l'ordine di attacco. Gli eroi con la velocit\195\160 pi\195\185 elevata possono sempre attaccare per primi."}, {title = "Percentuale di danno abilit\195\160 aumentata", content = "La percentuale di danno abilit\195\160 aumentata consente di ottenere un danno abilit\195\160 bonus maggiore. Pi\195\185 \195\168 alta la percentuale di danno abilit\195\160, maggiore \195\168 il danno provocato."}, {title = "Colpo", content = "Dalla precisione del colpo dipende la probabilit\195\160 di colpire gli eroi. Se un eroe ha un livello di precisione del colpo elevato, \195\168 meno probabile che manchi il bersaglio."}, {title = "Fuga", content = "La capacit\195\160 di fuga influisce sulla probabilit\195\160 di evitare i nemici. Se un eroe ha una capacit\195\160 di fuga elevata, \195\168 meno probabile che venga colpito."}, {title = "Colpo critico", content = "Pi\195\185 \195\168 alta la percentuale di colpo critico, maggiore \195\168 il danno critico e pi\195\185 elevata \195\168 la probabilit\195\160 di sferrare un colpo critico."}, {title = ",Danno critico", content = "Danno causato da un colpo critico. Con un colpo critico si causano danni maggiori."}, {title = "Lacerazione armatura", content = "Riduce di una certa percentuale la resistenza dell'armatura del bersaglio dell'attacco. Pi\195\185 \195\168 alto il livello di lacerazione dell'armatura, maggiore \195\168 la riduzione della resistenza dell'armatura."}, {title = "Percentuale di immunit\195\160 a controllo", content = "Riduce la probabilit\195\160 di essere controllati. Pi\195\185 \195\168 alta la percentuale di immunit\195\160 al controllo, minore \195\168 la probabilit\195\160 che gli eroi vengano controllati."}, {title = "Percentuale di riduzione danno", content = "Riduce la probabilit\195\160 di subire un danno. Pi\195\185 \195\168 alta la percentuale di riduzione danno, minore \195\168 il danno subito."}}}
help[4] = {title = "Come ottenere eroi", describe = {{title = "Trofeo battaglia", content = "La battaglia automatica, gli eventi di battaglia, la segreta degli eventi con la sfida dell'eroe e la torre del crepaccio fanno guadagnare frammenti di eroi."}, {title = "Acquisto nei negozi", content = "Puoi ottenere eroi e frammenti di eroi acquistandoli al mercato, nel negozio della fortuna, nel negozio delle pietre anima, nel negozio della prova dell'eroe, nel negozio della gilda e nel negozio della prova del campione."}, {title = "Cerchio dell'evocazione", content = "L'evocazione con cuori, pergamene di evocazione e diamanti nel cerchio dell'evocazione consente di ottenere velocemente nuovi eroi."}, {title = "Albero del profeta", content = "Puoi usare le gemme del profeta nell'albero del profeta per ottenere frammenti di eroi."}, {title = "Casin\195\178", content = "Puoi giocare i gettoni al casin\195\178 per ottenere frammenti di eroi."}, {title = "Missioni della taverna", content = "Nella taverna potrebbero essere disponibili missioni che fanno guadagnare frammenti di eroi. Portandole a termine, potrai ottenere determinati frammenti di eroi."}, {title = "Ricerca degli amici", content = "La ricerca degli eroi pu\195\178 farti ottenere determinati frammenti di eroi."}}}
help[5] = {title = "Come potenziare gli eroi", describe = {{title = "Trofeo battaglia", content = "La battaglia automatica, gli eventi di battaglia, la segreta degli eventi con la sfida dell'eroe e la torre del crepaccio fanno guadagnare frammenti di eroi."}, {title = "Acquisto nei negozi", content = "Puoi ottenere eroi e frammenti di eroi acquistandoli al mercato, nel negozio della fortuna, nel negozio delle pietre anima, nel negozio della prova dell'eroe, nel negozio della gilda e nel negozio della prova del campione."}, {title = "Cerchio dell'evocazione", content = "L'evocazione con cuori, pergamene di evocazione e diamanti nel cerchio dell'evocazione consente di ottenere velocemente nuovi eroi."}, {title = "Albero del profeta", content = "Puoi usare le gemme del profeta nell'albero del profeta per ottenere frammenti di eroi."}, {title = "Casin\195\178", content = "Puoi giocare i gettoni al casin\195\178 per ottenere frammenti di eroi."}, {title = "Missioni della taverna", content = "Nella taverna potrebbero essere disponibili missioni che fanno guadagnare frammenti di eroi. Portandole a termine, potrai ottenere determinati frammenti di eroi."}, {title = "Ricerca degli amici", content = "La ricerca degli eroi pu\195\178 farti ottenere determinati frammenti di eroi."}}}
help[6] = {title = "Categoria degli eroi", describe = {{title = "Classe", content = "Ci sono cinque classi: guerriero, mago, ranger, assassino e sacerdote. I guerrieri hanno HP e DIF elevati ma un attacco ridotto, il che consente loro di reggere bene l'attacco dei nemici. I maghi e i ranger hanno un livello pi\195\185 basso di HP e difesa ma un attacco elevato. Possono sopravvivere pi\195\185 a lungo e causare un maggior danno con il loro attacco se posizionati in seconda linea. Anche gli assassini hanno un livello ridotto di difesa e HP. I loro singoli attacchi causano un danno maggiore se si trovano in seconda linea. I sacerdoti hanno un livello ridotto di HP, difesa e attacco e devono essere posizionati in seconda linea. Gli incantesimi di guarigione e le abilit\195\160 di controllo sono molto utili."}, {title = "Livello di stelle", content = "Gli eroi hanno fino a un massimo di 5 stelle iniziali. Maggiore \195\168 il numero di stelle, pi\195\185 sono elevate le statistiche di base e potenti gli incantesimi. Le funzioni Combina e Risveglio possono aumentare il livello di stelle di un eroe."}, {title = "Fazione", content = "Ci sono 6 fazioni di eroi: oscurit\195\160, luce, foresta, ombra, fortezza e abisso. Limitazione rapporto: Foresta - Ombra - Fortezza - Abisso - Foresta, Oscurit\195\160 - Luce, Luce - Oscurit\195\160. Gli eroi con limitazione subiscono un danno del 30% pi\195\185 alto."}}}
help[7] = {title = "Equipaggiamento e artefatti", describe = {{title = "Qualit\195\160 dell'equipaggiamento e degli artefatti", content = "Sono disponibili equipaggiamento e artefatti di 6 qualit\195\160: blu, oro, viola, verdi, rossi e arancioni. Pi\195\185 \195\168 alta la qualit\195\160, maggiori saranno le statistiche bonus."}, {title = "Livello di stelle di equipaggiamento e artefatti", content = "Il livello massimo di stelle dell'equipaggiamento blu e oro \195\168 2; di quello viola \195\168 3; di quello verde o superiore \195\168 4. Il livello massimo di stelle degli artefatti blu e oro \195\168 4; di quelli viola e verdi \195\168 5; di quelli rossi o superiori \195\168 6. Tra l'equipaggiamento e gli artefatti della stessa qualit\195\160, quelli con il pi\195\185 alto livello di stelle hanno maggiori statistiche bonus."}, {title = "Combina equipaggiamento e artefatti", content = "Dal fabbro puoi combinare equipaggiamento di basso livello per ottenerne di alto livello. Inoltre, se disponi di un numero sufficiente di frammenti, puoi combinare gli artefatti."}, {title = "Ottieni equipaggiamento", content = "Puoi ottenere equipaggiamento con la battaglia automatica, nella Torre dell'oblio e nell'evento di battaglia. Puoi inoltre acquistare l'equipaggiamento al mercato, nel negozio della prova dell'eroe, nel negozio della gilda e nel negozio della fortuna. Hai la possibilit\195\160 di trovare dell'equipaggiamento anche nel pozzo dei desideri."}, {title = "Set di equipaggiamento", content = "L'equipaggiamento di qualit\195\160 superiore a quella verde e con pi\195\185 di 3 stelle ha effetti specifici se combinato. Indossando il numero necessario di pezzi di un set, si attivano tali effetti specifici."}, {title = "Potenzia artefatto", content = "Puoi utilizzare gli artefatti inutilizzati per potenziare quelli in uso. Il potenziamento degli artefatti ne aumenta il livello di stelle."}, {title = "Ottieni artefatto", content = "Come ottenere artefatti 1. Combina gli artefatti con i frammenti ottenuti durante la battaglia automatica 2. Combina gli artefatti con i frammenti ottenuti nel negozio della gilda 3. Al casin\195\178 hai l'opportunit\195\160 di ottenere artefatti."}}}
help[8] = {title = "Gilda", describe = {{title = "Unisciti alla gilda", content = "1. Inserisci l'ID della gilda nel campo di ricerca delle gilde; 2. Tocca Candidati per inviare la richiesta; 3. Entri a far parte della gilda quando la richiesta viene approvata."}, {title = "Crea gilda", content = "Per creare una gilda nella relativa pagina, devi aver raggiunto il livello previsto e disporre di un numero sufficiente di diamanti. Dopo aver creato la gilda, puoi invitare gli altri giocatori a farne parte."}, {title = "Nomina comandante", content = "Il comandante della gilda pu\195\178 toccare Nomina comandante nel menu dei membri della gilda per assegnare la carica a un altro membro della gilda."}, {title = "Scarta gilda", content = "Il comandante della gilda pu\195\178 eliminare la gilda dalla pagina delle gilde. La gilda viene eliminata dopo 2 ore dalla selezione dell'opzione."}, {title = "Livello della gilda", content = "Effettuando quotidianamente l'accesso, i membri della gilda possono farla avanzare."}, {title = "Attivit\195\160 di gioco associate alla gilda", content = "Per ottenere monete gilda e altri oggetti, il membro della gilda pu\195\178 entrare nelle segrete della gilda e nel mulino e partecipare alla guerra tra gilde."}, {title = "Negozio della gilda", content = "I membri della gilda possono utilizzare le monete gilda per acquistare frammenti di eroi, equipaggiamento e frammenti di artefatti."}, {title = "Scuola delle scienze della gilda", content = "I membri della gilda possono potenziare le abilit\195\160 gilda con monete gilda e oro nella scuola delle scienze della gilda. Il potenziamento delle abilit\195\160 pu\195\178 rendere pi\195\185 forti gli eroi corrispondenti."}, {title = "Funzionamento della gilda", content = "I membri della gilda possono farla avanzare effettuando quotidianamente l'accesso. Per ottenere monete gilda e altri oggetti, possono entrare nelle segrete della gilda e nel mulino e partecipare alla guerra tra gilde. Possono inoltre utilizzare monete gilda per ottenere oggetti e potenziare le abilit\195\160 gilda nella scuola delle scienze della gilda."}}}
help[9] = {title = "Amici", describe = {{title = "Aggiungi amico", content = "Inserisci l'ID di un amico per cercarlo; invia una richiesta agli amici consigliati; tocca l'avatar del giocatore nella chat per stringere amicizia; tocca l'avatar del giocatore nella gilda per stringere amicizia."}, {title = "Richiesta di amicizia", content = "Controlla le richieste di amicizia ricevute nell'elenco delle richieste e tocca Accetta per stringere amicizia."}, {title = "Cuore", content = "Gli amici possono inviarsi reciprocamente dei cuori. I cuori possono essere usati per l'evocazione gratuita nel Cerchio dell'evocazione."}, {title = "Elimina amico", content = "1. Tocca l'avatar dell'amico nella pagina degli amici per aprirlo; 2. Tocca Elimina amico per rimuoverlo."}, {title = "Assistenza amico", content = "Ogni giocatore ha 1 opportunit\195\160 di ricerca ogni 8 ore. Con la ricerca si possono trovare mostri con bottino, oro e frammenti di eroi. Aiutando gli amici a sconfiggere i mostri con bottino, otterrai ricompense e punti casuali. Ogni settimana saranno offerte delle ricompense in base ai punti conquistati."}}}
return help

