global.tutorial_text_1 = {string = "Sava\197\159\195\167\196\177lar\196\177n topra\196\159\196\177na ho\197\159 geldiniz, \195\182nce kendinize yak\196\177\197\159an bir isim verin!"}
global.tutorial_text_2 = {string = "Ne g\195\188zel bir isim! Sana etraf\196\177 gezdireyim"}
global.tutorial_text_3 = {string = "\195\150ncelikle b\195\188y\195\188 dizili\197\159ini a\195\167\196\177klayay\196\177m, buradan \195\167ok g\195\188\195\167l\195\188 kahramanlar \195\167a\196\159\196\177rabilirsiniz"}
global.tutorial_text_4 = {string = "Ana \195\135a\196\159\196\177r ile e\197\159ya kalitesi d\195\188\197\159\195\188k olan kahraman \195\167a\196\159r\196\177labilir (1 y\196\177ld\196\177z, 2 y\196\177ld\196\177z, 3 y\196\177ld\196\177z, 4 y\196\177ld\196\177z, 5 y\196\177ld\196\177z), ana \195\135a\196\159\196\177r i\197\159lemi i\195\167in ana \195\135a\196\159\196\177r kitab\196\177 kullan\196\177lmas\196\177 gerekir"}
global.tutorial_text_5 = {string = "Tamam, bir deneyelim bakal\196\177m, bu sefer \195\188cretsiz"}
global.tutorial_text_6 = {string = "Y\195\188ksek seviye \195\135a\196\159\196\177r ile e\197\159ya kalitesi y\195\188ksek olan kahraman \195\167a\196\159r\196\177labilir (3 y\196\177ld\196\177z, 4 y\196\177ld\196\177z, 5 y\196\177ld\196\177z), y\195\188ksek seviye \195\135a\196\159\196\177r i\197\159lemi i\195\167in y\195\188ksek seviye \195\135a\196\159\196\177r kitab\196\177 kullan\196\177lmas\196\177 gerekir"}
global.tutorial_text_7 = {string = "Bir deneyelim bakal\196\177m, bu sefer \195\188cretsiz"}
global.tutorial_text_8 = {string = "Vay, g\195\182r\195\188n\195\188\197\159e g\195\182re kahraman \195\135a\196\159\196\177r konusunda \195\167ok iyisiniz, bir sonraki a\195\167\196\177klanmak istenen boyut kap\196\177s\196\177, birlikte gidip g\195\182z atmaya ne dersiniz?"}
global.tutorial_text_9 = {string = "Bu boyut kap\196\177s\196\177 \195\188zerinden, sava\197\159 b\195\182lgesine ilerleyin, ve birlikte sava\197\159\196\177n"}
global.tutorial_text_10 = {string = "Sava\197\159 ba\197\159lamadan \195\182nce, sava\197\159acak kahraman\196\177n belirlenmesi gerekmektedir"}
global.tutorial_text_11 = {string = "Kahraman resmine t\196\177klay\196\177n, sava\197\159acak kahraman\196\177 se\195\167in"}
global.tutorial_text_12 = {string = "Haz\196\177r olun, sava\197\159\196\177n!"}
global.tutorial_text_13 = {string = "Sava\197\159a giden kahramanlar otomatik olarak sava\197\159abilir, bu s\195\188re aral\196\177\196\159\196\177nda da sava\197\159 ganimeti kazan\196\177l\196\177r!"}
global.tutorial_text_14 = {string = "A\197\159a\196\159\196\177daki geli\197\159im \195\167ubu\196\159una bak\196\177n, \197\159imdi bu b\195\182l\195\188m\195\188 aramaktay\196\177z, arama i\197\159lemi sona erdikten sonra 1 sava\197\159 tetiklenir"}
global.tutorial_text_15 = {string = "Sava\197\159 tetiklendi! Gidip bakal\196\177m!"}
global.tutorial_text_16 = {string = "\195\150nce, sava\197\159a g\195\182nderilecek kahraman\196\177 se\195\167in"}
global.tutorial_text_17 = {string = "Sava\197\159 s\196\177ras\196\177nda, kahraman resmini s\195\188r\195\188kleyerek konumunu de\196\159i\197\159tirebilirsiniz"}
global.tutorial_text_18 = {string = "Haz\196\177r olun, ilk sava\197\159\196\177m\196\177z\196\177 yapal\196\177m!"}
global.tutorial_text_19 = {string = "\195\150n s\196\177ra 2 konum b\195\188y\195\188k miktarda sald\196\177r\196\177ya u\196\159rayacak, savunma becerisi y\195\188ksek olan kahramanlar\196\177n\196\177z\196\177 ayarlay\196\177n"}
global.tutorial_text_20 = {string = "Kahraman sald\196\177r\196\177s\196\177 ya da ald\196\177\196\159\196\177 zarar enerji kazan\196\177m\196\177 sa\196\159lar, enerji de\196\159eri dolduktan sonra beceri serbest kal\196\177r."}
global.tutorial_text_21 = {string = "Harika! Kahramanlar\196\177n par\195\167alar ile birle\197\159tirlebilece\196\159ini, biliyor muydunuz. Bu i\197\159lemin nas\196\177l yap\196\177ld\196\177\196\159\196\177n\196\177 hep birlikte \195\182\196\159renelim"}
global.tutorial_text_22 = {string = "Tehlikeli maceralar i\195\167in g\195\188\195\167l\195\188 kahramanlara sahip olmak \195\167ok \195\182nemlidir, hadi nas\196\177l kahraman yeti\197\159rilebilece\196\159imize bir bakal\196\177m"}
global.tutorial_text_23 = {string = "Gereksiz kahramanlar da\196\159\196\177t\196\177l\196\177p kahraman ruhu olabilir, kahraman ruhlar\196\177 kahraman\196\177n geli\197\159tirilmesi i\195\167in \195\182nem arz eden malzemelerdir"}
global.tutorial_text_24 = {string = "L\195\188tfen Da\196\159\196\177tk istedi\196\159iniz kahraman\196\177 se\195\167iniz"}
global.tutorial_text_25 = {string = "Fena de\196\159il, \197\159imdi gidelim ve kahraman\196\177 geli\197\159tirelim"}
global.tutorial_text_26 = {string = "Kahramanlar yeterli EXP miktar\196\177na ula\197\159t\196\177\196\159\196\177nda, buraya t\196\177klayarak geli\197\159tirme i\197\159lemi yap\196\177labilir"}
global.tutorial_text_27 = {string = "G\195\188zel, Kahramanlara biraz ekipman donatal\196\177m"}
global.tutorial_text_28 = {string = "Silah ikonuna t\196\177klayarak, silah se\195\167imi yap\196\177n"}
global.tutorial_text_29 = {string = "M\195\188kemmel! \197\158imdi daha da g\195\188\195\167lendiniz!"}
global.tutorial_text_30 = {string = "Maceram\196\177za devam edebiliriz"}
global.tutorial_text_31 = {string = "Yeni sahnelerin kilitlerinin a\195\167\196\177lmas\196\177 i\195\167in g\195\188\195\167l\195\188 bir otomatik sava\197\159 dizili\197\159i gerekmektedir, hadi biraz daha kahraman g\195\182nderelim"}
global.tutorial_text_32 = {string = "Harika, otomatik sava\197\159 listesi kilidi seviyeniz y\195\188kseldik\195\167e a\195\167\196\177lacakt\196\177r, kahramanlar\196\177 zaman\196\177nda kullan\196\177n"}
global.tutorial_text_33 = {string = "Buraya t\196\177klayarak otomatik sava\197\159 ganimeti \195\167ekebilirsiniz, s\195\188re ne kadar uzarsa sava\197\159 ganimeti o kadar zenginle\197\159ir."}
global.tutorial_text_34 = {string = "Art\196\177k yeterince g\195\188\195\167l\195\188s\195\188n\195\188z, bilinmeyen d\195\188nyada sizi bir u\195\167urum bekliyor olarak, iyi \197\159anslar! G\195\182r\195\188\197\159mek \195\188zere!"}
global.tutorial_text_35 = {string = "Karaborsa a\195\167\196\177ld\196\177, birbirinden de\196\159erli hazineler, sizleri bekliyor!"}
global.tutorial_text_36 = {string = "Kumarhaneye ho\197\159 geldiniz, cesur asker, \197\159ans\196\177n\196\177z\196\177 denemek ister misiniz?"}
global.tutorial_text_37 = {string = "Bir hesap a\195\167arak, oyuna farkl\196\177 cihazlardan giri\197\159 yapabilirsiniz"}
global.tutorial_text_38 = {string = "Di\196\159er oyuncularla birlikte arena i\195\167erisinde onurlu bir \197\159ekilde sava\197\159\196\177n"}
global.tutorial_text_39 = {string = "L\195\188tfen arenada savunma dizili\197\159i ayarlay\196\177n"}
global.tutorial_text_40 = {string = "Hayal kulesi a\195\167\196\177ld\196\177, sonsuz hazineler sizleri bekliyor, tabiki tehlikelere dikkat edilmesi gerekmektedir"}
