help[1] = {title = "Binalar\196\177n Fonksiyonu", describe = {{title = "Sava\197\159", content = "Kahraman birli\196\159i sava\197\159a g\195\182nderilir. Otomatik sava\197\159 i\197\159lemi ba\197\159lat\196\177l\196\177r ve sava\197\159 tamamlan\196\177r. Sava\197\159 s\196\177ras\196\177nda EXP, Alt\196\177n, Ekipman v.b. \195\182d\195\188ller kazanabilirsiniz. Etkinlik tarihlerinde d\195\188zensizlik olmas\196\177 halinde \195\167ift kat d\195\188\197\159me s\195\188resi devreye girecektir."}, {title = "Cesur Denemesi", content = "Aral\196\177ks\196\177z ve amans\196\177z sava\197\159larda, farkl\196\177 sunuculardaki oyunculara meydan okuyabilirsiniz. Kahramanlar\196\177n HP de\196\159erleri bu deneme s\196\177ras\196\177nda iyile\197\159mez. Bu y\195\188zden olduk\195\167a stratejik davranman\196\177z gerekmektedir!"}, {title = "Hayal Kulesi", content = "Hayal Kulesi her kat\196\177nda muhaf\196\177zlar yer almaktad\196\177r. Onlara kar\197\159\196\177 zafer kazan\196\177rsan\196\177z b\195\188y\195\188k miktarda Kahraman Geli\197\159tirme Ta\197\159\196\177 ve g\195\188\195\167l\195\188 ekipmanlardan kazan\196\177rs\196\177n\196\177z. Belirli bir kata ula\197\159t\196\177ktan sonra 5 y\196\177ld\196\177zl\196\177 kahraman kazanabilirsiniz. Daha fazla kat say\196\177s\196\177 daha fazla sava\197\159 demektir."}, {title = "Arena", content = "Arena i\195\167erisindeki di\196\159er oyunculara kar\197\159\196\177 meydan okuyun! Zafer kazan\196\177n, puanlar\196\177 toplay\196\177n. B\195\188y\195\188k miktarda \195\188cretsiz Elmas kazan\196\177ld\196\177! Her Cuma, Cumartesi, Pazar (Sunucu S\195\188resi) \197\158ampiyon Denemesi a\195\167\196\177l\196\177r. Farkl\196\177 sunuculardan gelen oyunculara kar\197\159\196\177 sava\197\159\196\177n!"}, {title = "Dizili\197\159 \195\167a\196\159\196\177r", content = "Yeni kahramanlar \195\167ekmek en h\196\177zl\196\177 yoldur. Daha fazla Kalp ya da \195\135a\196\159\196\177rma Kitab\196\177 toplayarak \195\188cretsiz kahraman \195\167a\196\159\196\177rma i\197\159lemi yapabilirsiniz. Enerji \195\167a\196\159\196\177rma dolduktan sonra herhangi 1 adet 5 y\196\177ld\196\177zl\196\177 kahraman kazanabilirsiniz."}, {title = "Dizili\197\159 olu\197\159tur", content = "Kahraman y\196\177ld\196\177z\196\177 geli\197\159tirmek i\195\167in zorunludur. Sava\197\159 dizili\197\159i s\196\177ras\196\177nda kahraman y\196\177ld\196\177z seviyesi geli\197\159tirilebilir. En fazla 6 y\196\177ld\196\177zd\196\177r. Gereken t\195\188m malzemeleri toplayan kahramanlar sava\197\159 dizili\197\159i s\196\177ras\196\177nda kahraman y\196\177ld\196\177z seviyelerini geli\197\159tirebilirler. Y\196\177ld\196\177z geli\197\159tirdikten sonra malzeme t\195\188ketimi yapan kahraman kar\197\159\196\177l\196\177k gelen de\196\159erde Kahraman ve Kahraman Geli\197\159im Ta\197\159\196\177 alacaklard\196\177r. Donat\196\177lan t\195\188m di\196\159er ekipmanlar \195\167antaya geri d\195\182ner. Mevcut kahraman ne kadar fazla ise, kahraman y\196\177ld\196\177z geli\197\159tirme f\196\177rsat\196\177 o kadar fazla olur!"}, {title = "Sunak", content = "Kullan\196\177lmayan kahramanlar\196\177n\196\177z varsa, Sunak b\195\182lgesine giderek onlar\196\177 da\196\159\196\177tabilirsiniz. Kahraman da\196\159\196\177tarak belirli miktarda Elit Ruh, Kahraman Geli\197\159im Ta\197\159\196\177, Sihirli Toz, Ruh Ta\197\159\196\177 v.b. malzemeler \195\167ekilebilir. Da\196\159\196\177t\196\177lan kahraman\196\177n Ekipman, Kristal ve Gizemli Silahlar\196\177 \195\167antaya geri g\195\182nderilir. Da\196\159\196\177t\196\177lan kahraman taraf\196\177ndan kazan\196\177lan Ruh Ta\197\159\196\177 Sunak Marketi \195\188zerinden kahraman ile takas edilebilir! Ruh Ta\197\159\196\177 oldu\196\159u zaman etkinlik \195\188zerinden sat\196\177\197\159\196\177 yap\196\177lan hediye paketleri kazan\196\177labilir!"}, {title = "Demirci", content = "Demirci \195\188zerinden d\195\188\197\159\195\188k seviyeli ekipman birle\197\159tirerek y\195\188ksek seviyeli ekipman elde edilebilir. Ekipman seti donat\196\177ld\196\177\196\159\196\177nda ekipman\196\177n \195\182zellikleri aktif olur! Dikkat: Donat\196\177lm\196\177\197\159 olan ekipman tekrar Demirci d\195\188kkan\196\177na giremez."}, {title = "Dilek Havuzu", content = "Dilek Havuzu \195\188zerinde Dilek Paras\196\177 t\195\188keterek Kahraman Par\195\167as\196\177, Ekipman, Gizemli Silah, Alt\196\177n, Elit Ruh v.b. e\197\159yalar kazan\196\177labilir. \196\176yi \197\159anslar!"}, {title = "Market", content = "Market belirli s\195\188relerde yenilenmektedir (Elle yenileme de yapabilirsiniz) ve Elit Ruh, Elit Ruh Geli\197\159im Ta\197\159\196\177, Kahraman Par\195\167as\196\177, Ekipman, \195\135a\196\159\196\177rma Kitab\196\177, Arena Bileti, \195\135ip v.b. e\197\159yalar kazan\196\177l\196\177r."}, {title = "Efendi A\196\159ac\196\177", content = "Efendi A\196\159ac\196\177 \195\188zerinde belirli kamplar \195\167a\196\159r\196\177labilir! Efendi Safir kullanarak belirli kamplar\196\177n \195\167a\196\159\196\177rma kap\196\177s\196\177nda \195\167a\196\159\196\177rma i\197\159lemi yap\196\177labilir. 4 y\196\177ld\196\177zl\196\177 Kamp, 5 y\196\177ld\196\177zl\196\177 Kahraman, 4 ve 5 y\196\177ld\196\177zl\196\177 Evrensel Par\195\167a kazanma olas\196\177l\196\177\196\159\196\177 bulunmaktad\196\177r. (Efendi Safiri, Meyhane G\195\182revi \195\188zerinden kazan\196\177labilir, ayr\196\177ca Etkinlik Takas\196\177 yaparak da elde edilebilir. D\195\188zensiz s\195\188reli etkinlikler s\196\177ras\196\177nda Efendi Safiri sat\196\177lmaktad\196\177r) Her \195\167a\196\159\196\177rma i\197\159lemi sonras\196\177 ekstra Efendi Bereketi kazan\196\177l\196\177r. Belirli miktarda Efendi Bereketi t\195\188keterek bir kamp\196\177n kahramanlar\196\177n\196\177 ayn\196\177 kamp\196\177n kahramanlar\196\177 olarak ayarlayabilirsiniz. Dikkat: Karanl\196\177k Kamp'a ait olmayan sadece 4 ve 5 y\196\177ld\196\177zl\196\177 kahramanlar yeniden ayarlanabilir."}, {title = "Meyhane", content = "Meyhane \195\188zerinde her 24 saatte bir farkl\196\177 y\196\177ld\196\177z seviyelerinde kahraman g\195\182revi yenilenir. Kahraman g\195\182revini tamamlayarak zengin \195\182d\195\188llerin sahibi olabilirsiniz! Elmas harcayarak g\195\182revin y\196\177ld\196\177z seviyesi de\196\159i\197\159tirilebilir. Ayr\196\177ca g\195\182rev daha h\196\177zl\196\177 tamamlan\196\177r. Meyhane G\195\182rev Kitab\196\177 kullanarak kahraman g\195\182revi art\196\177r\196\177labilir (Yenileme sonras\196\177 kullan\196\177lmayan kitaplar yeniden ayarlanabilir). Meyhane G\195\182revi Kitab\196\177 otomatik sava\197\159 s\196\177ras\196\177nda kazanma olas\196\177l\196\177\196\159\196\177 vard\196\177r."}}}
help[2] = {title = "B\195\182l\195\188m A\195\167\196\177klamas\196\177", describe = {{title = "Genel G\195\182r\195\188n\195\188m", content = "Sava\197\159 haritalar\196\177 Normal, Kabus, Cehennem, \195\150l\195\188m \197\159eklinde 4 zorluk derecesine ayr\196\177lmaktad\196\177r. Her zorluk derecesinde bir \195\167ok b\195\182l\195\188m yer almaktad\196\177r. S\196\177ras\196\177yla 3 adet zorluk derecesinin a\195\167\196\177lmas\196\177 gerekmektedir."}, {title = "B\195\182l\195\188m kilidini a\195\167", content = "Yeni b\195\182l\195\188me gelip yeterli olarak ke\197\159if s\195\188resi i\195\167in beklendi\196\159inde sava\197\159 tetiklenir. Sava\197\159\196\177 tamamlayarak sava\197\159 g\195\188c\195\188 ve seviyeniz bir sonraki b\195\182l\195\188me girmek i\195\167in yeterli seviyeye ula\197\159t\196\177\196\159\196\177nda, sonraki b\195\182l\195\188me ilerlemek i\195\167in t\196\177klanabilir."}, {title = "B\195\182lge & Harita Da\196\159\196\177tma", content = "Bir b\195\182lgedeki t\195\188m b\195\182l\195\188mleri ge\195\167tikten sonra, bir sonraki b\195\182lgenin kilidi a\195\167\196\177l\196\177r. Herhangi bir zorluk modunda t\195\188m b\195\182l\195\188mleri a\197\159t\196\177ktan sonra bir sonraki zorluk modu kilidi a\195\167\196\177l\196\177r."}, {title = "Sava\197\159 Olay\196\177", content = "Yeni bir b\195\182l\195\188me girerek bu b\195\182l\195\188me ait olan sava\197\159 tetiklenir. B\195\182l\195\188mdeki sava\197\159 ge\195\167ildikten sonra zengin \195\182d\195\188ller kazan\196\177l\196\177r."}, {title = "Birlik", content = "Birlik i\195\167erisinde k\195\188\195\167\195\188k bir kahraman tak\196\177m\196\177 ayarlanabilir. Ayarlanabilen birlik ki\197\159i say\196\177s\196\177 seviyenize g\195\182re artar. Kahraman sava\197\159 g\195\188c\195\188 ne kadar y\195\188ksek olursa, sava\197\159a giden kahraman say\196\177s\196\177 ve birlik sava\197\159 g\195\188c\195\188 o kadar fazla olur. (Sava\197\159a giden kahraman\196\177n sava\197\159 g\195\188c\195\188 sadece kilidi a\195\167\196\177k olan b\195\182l\195\188m ile ilgilidir. D\195\188\197\159me durumu ile ilgisi yoktur)"}, {title = "Otomatik", content = "Otomatik sava\197\159tan elde edilen kazan\195\167lar baz\196\177 s\195\188relerde art\196\177\197\159 g\195\182sterir. En uzun 8 saat boyunca otomatik sava\197\159 yapabilirsiniz. 8 saatin sonunda yap\196\177lan otomatik sava\197\159tan kazan\195\167 elde edilmez. Bu y\195\188zden 8 saat i\195\167erisinde gelip otomatik sava\197\159tan elde edilen kazan\195\167lar\196\177 \195\167ekmeyi unutmay\196\177n\196\177z!"}, {title = "\195\156retim & \195\135ekme", content = "B\195\182l\195\188mlerde belirli miktarda EXP, Alt\196\177n ve Elit Ruh elde edilir. Otomatik sava\197\159tan 3 t\195\188r kazan\195\167 \195\167ekilebilir. B\195\182l\195\188m\195\188n zorluk derecesi ne kadar y\195\188ksek olursa bu kazan\195\167lar\196\177n miktar\196\177 o kadar fazla olur. VIP seviyesine kar\197\159\196\177l\196\177k gelecek \197\159ekilde d\195\188\197\159en bu 3 kayna\196\159\196\177n miktar\196\177 artar."}, {title = "D\195\188\197\159en & Ganimet", content = "E\197\159ya d\195\188\197\159me bilgisinden mevcut b\195\182l\195\188mden d\195\188\197\159ebilecek olan e\197\159yalara bakabilirsiniz. \196\176lerleyip herhangi bir b\195\182l\195\188mde otomatik sava\197\159 ba\197\159latt\196\177\196\159\196\177n\196\177z zaman, bu b\195\182l\195\188mden d\195\188\197\159en e\197\159yalar kazan\196\177lacakt\196\177r. Kazan\196\177lm\196\177\197\159 olan d\195\188\197\159en e\197\159yalara sava\197\159 ganimetlerinden bak\196\177labilir."}}}
help[3] = {title = "Sava\197\159", describe = {{title = "Sava\197\159 Konumu", content = "Normalde \195\182n hatta duran kahramanlar\196\177n sald\196\177r\196\177ya u\196\159ramas\196\177 arka s\196\177rada duranlara g\195\182re \195\167ok daha kolayd\196\177r. Kahramanlar\196\177n mesleklerine g\195\182re sava\197\159 pozisyonlar\196\177n\196\177 ayarlaman\196\177z gerekmektedir. Bu \197\159ekilde sava\197\159 alan\196\177nda hayatta kalma s\195\188reniz \195\167ok daha uzun olacakt\196\177r."}, {title = "Kamp Kontrol\195\188", content = "Kahramanlar aras\196\177nda kamp kontrol ba\196\159lant\196\177s\196\177 bulunmaktad\196\177r. Sava\197\159 \195\182ncesinde d\195\188\197\159man\196\177n sava\197\159 dizili\197\159ine g\195\182re kahramanlar\196\177n ayarlanmas\196\177 sava\197\159 esnas\196\177nda size avantaj sa\196\159layacakt\196\177r."}, {title = "Kamp I\197\159\196\177k Halkas\196\177", content = "6 adet ayn\196\177 kamptan olan kahraman ya da 6 adet tamamen farkl\196\177 kamplardan olan kahramanlar\196\177n t\195\188m\195\188 g\195\188\195\167l\195\188 bir \196\177\197\159\196\177k halkas\196\177 etkisini aktif edecektir."}, {title = "Sava\197\159 Kazanan/Kaybedenin Belirlenmesi", content = "Sava\197\159 turu say\196\177s\196\177n\196\177n 15 \195\188zerinde olmas\196\177 savunan taraf\196\177n ba\197\159ar\196\177s\196\177zl\196\177\196\159\196\177n\196\177 belirleyecektir. Eleme turunda t\195\188m d\195\188\197\159man\196\177 \195\182ld\195\188ren kahraman sava\197\159\196\177n galibi olur. E\196\159er size ait olan kahramanlar\196\177n t\195\188m\195\188 d\195\188\197\159man taraf\196\177ndan \195\182ld\195\188r\195\188l\195\188rse sava\197\159 kaybedilir."}, {title = "Sald\196\177r\196\177", content = "Kahraman sald\196\177r\196\177s\196\177 ne kadar fazla olursa, d\195\188\197\159mana verilen zarar o kadar fazla olur."}, {title = "HP", content = "Kahraman HP de\196\159eri ne kadar fazla ise, sava\197\159 s\196\177ras\196\177nda hayatta kalma s\195\188resi o kadar uzun olur."}, {title = "Z\196\177rh", content = "Kahraman koruma kalkan\196\177 ne kadar y\195\188ksek olursa, d\195\188\197\159man\196\177n kahramana verdi\196\159i zarar o kadar az olur."}, {title = "H\196\177z", content = "H\196\177z her turdaki kahraman\196\177n \195\167\196\177k\196\177\197\159 s\196\177ras\196\177n\196\177 belirlemektedir. Kahraman ne kadar h\196\177zl\196\177 olursa \195\167\196\177k\196\177\197\159 y\195\188ksek oldu\196\159undan dolay\196\177 \195\182n s\196\177rada yer al\196\177r."}, {title = "Beceri Zarar Oran\196\177 Artt\196\177rma", content = "Beceri zarar oran\196\177n\196\177n artmas\196\177 becerinin verdi\196\159i zarar\196\177 ekstra art\196\177racakt\196\177r. Beceri zarar oran\196\177 ne kadar y\195\188ksek olursa, verdi\196\159i zarar o kadar fazla olur."}, {title = "\196\176sabet", content = "\196\176sabet, d\195\188\197\159mana yap\196\177lan sald\196\177r\196\177n\196\177n isabet oran\196\177n\196\177 ifade etmektedir. \196\176sabet ne kadar y\195\188ksek olursa, isabetin ba\197\159ar\196\177 oran\196\177 o kadar y\195\188ksek olur."}, {title = "Ka\195\167\196\177\197\159", content = "Ka\195\167\196\177\197\159, kahraman\196\177n sald\196\177r\196\177dan isabet alma oran\196\177n\196\177 etkiler. Ka\195\167\196\177\197\159 ne kadar y\195\188ksek olursa d\195\188\197\159man taraf\196\177ndan yap\196\177lan sald\196\177r\196\177n\196\177n isabet oran\196\177 d\195\188\197\159er."}, {title = "Kritik Sald\196\177r\196\177", content = "Kritik Sald\196\177r\196\177 tetiklenme oran\196\177d\196\177r. Kahraman kritik sald\196\177r\196\177s\196\177 ne kadar y\195\188ksek olursa, kritik sald\196\177r\196\177 tetiklenme olas\196\177l\196\177\196\159\196\177 o kadar b\195\188y\195\188k olur."}, {title = "Kritik Vuru\197\159 Zarar\196\177", content = "Kritik sald\196\177r\196\177 tetiklendi\196\159i zaman zarar olu\197\159ur, kahraman kritik sald\196\177r\196\177s\196\177ndan kaynaklanan zarar ne kadar y\195\188ksek olursa kritik sald\196\177r\196\177 ba\197\159ar\196\177yla tetiklendikten sonra olu\197\159an zarar o kadar fazla olur."}, {title = "Kalkan K\196\177rma", content = "Sald\196\177r\196\177lan hedefin kalkan de\196\159erine kar\197\159\196\177 koyulur. Kahraman kalkan k\196\177rma de\196\159eri ne kadar y\195\188kse olursa, d\195\188\197\159man kalkan\196\177na o kadar \195\167ok kar\197\159\196\177 koyulabilir."}, {title = "Serbest Kontrol Oran\196\177", content = "D\195\188\197\159man taraf\196\177ndan kontrol edilemez. Kahraman\196\177n serbest kontrol\195\188 ne kadar y\195\188ksek olursa, d\195\188\197\159man taraf\196\177ndan kontrol edilme oran\196\177 o kadar d\195\188\197\159er."}, {title = "Zarar Azalma Oran\196\177", content = "Verilen zarar oran\196\177 d\195\188\197\159er. Kahraman\196\177n zarar d\195\188\197\159me oran\196\177 ne kadar y\195\188ksek ise, d\195\188\197\159man taraf\196\177ndan verilen zarar o kadar azal\196\177r."}}}
help[4] = {title = "Kahraman \195\135ekme Y\195\182ntemleri", describe = {{title = "D\195\188\197\159en E\197\159yalar", content = "Otomatik sava\197\159 s\196\177ras\196\177nda, sava\197\159 ge\195\167ilir, her g\195\188n etkinlik ek sahnesine kat\196\177l\196\177m yap\196\177l\196\177r\226\128\148\226\128\148Kahraman\196\177n sava\197\159mas\196\177 ve Hayal Kulesi'ne sald\196\177rmas\196\177 ile kahraman par\195\167alar\196\177 d\195\188\197\159er."}, {title = "Market Sat\196\177n Alma", content = "Market, \197\158ans Marketi, Ruh Ta\197\159\196\177 Marketi, Cesur Deneme Marketi, Birlik Marketi, \197\158ampiyon Deneme Marketi birimlerinde kahraman ve kahraman par\195\167alar\196\177 sat\196\177\197\159\196\177 yap\196\177lmaktad\196\177r."}, {title = "Dizili\197\159 \195\167a\196\159\196\177r", content = "\195\135a\196\159\196\177rma i\197\159lemi s\196\177ras\196\177nda Kalp, \195\135a\196\159\196\177rma Kitab\196\177, Elmas v.b. e\197\159yalar\196\177 kullanarak \195\167a\196\159\196\177rma i\197\159lemi yapabilir ve h\196\177zl\196\177 bir \197\159ekilde yeni kahramanlardan \195\167ekebilirsiniz."}, {title = "Efendi A\196\159ac\196\177", content = "Efendi A\196\159ac\196\177 b\195\182lgesinde Efendi Safiri t\195\188ketip kahraman par\195\167as\196\177 elde edebilirsiniz!"}, {title = "Dilek Havuzu", content = "Dilek Havuzu b\195\182lgesinde Dilek Paras\196\177 harcay\196\177p yap\196\177lan Dilek ile kahraman par\195\167as\196\177 kazanma olas\196\177l\196\177\196\159\196\177 vard\196\177r."}, {title = "Meyhane G\195\182revi", content = "Meyhane \195\188zerinde kahraman par\195\167as\196\177 \195\182d\195\188l\195\188 i\195\167in kahraman g\195\182revi g\195\182r\195\188nme olas\196\177l\196\177\196\159\196\177 vard\196\177r. Kahraman g\195\182revini tamamlad\196\177ktan sonra belirli miktarda kahraman par\195\167as\196\177 kazan\196\177l\196\177r."}, {title = "Arkada\197\159 Arama", content = "Arkada\197\159 aramas\196\177 yaparak belirli olas\196\177l\196\177kla belirli say\196\177da kahraman par\195\167as\196\177 kazanma \197\159ans\196\177 vard\196\177r."}}}
help[5] = {title = "Kahraman\196\177n G\195\188\195\167lenmesini Sa\196\159layan Y\195\182ntemler", describe = {{title = "Kahraman Seviye Geli\197\159tirme", content = "Elit Ruh t\195\188keterek kahraman\196\177n seviyesi geli\197\159tirilir."}, {title = "Kahraman Geli\197\159tirme", content = "Kahraman seviyesi \195\188st limite ula\197\159t\196\177\196\159\196\177 zaman, Kahraman Geli\197\159im Ta\197\159\196\177 t\195\188keterek kahraman\196\177n kalitesi art\196\177r\196\177labilir."}, {title = "Kahraman Y\196\177ld\196\177z Geli\197\159tirme", content = "Kahraman seviyesi ve kalitesi dolduktan sonra, kurulan dizili\197\159ten y\196\177ld\196\177z seviyesi geli\197\159tirilebilir. Y\196\177ld\196\177z seviyesi y\195\188kseldikten sonra kahraman \195\182zellikleri ve seviyesi artar. Ekipman ve kaliteler korunur."}, {title = "Kahraman Uyan\196\177\197\159\196\177", content = "Malzeme t\195\188keterek 6 y\196\177ld\196\177z \195\188zeri kahraman kullan\196\177l\196\177p uyan\196\177\197\159 ger\195\167ekle\197\159tirilir. Uyan\196\177\197\159 i\197\159lemi kahraman\196\177n y\196\177ld\196\177z seviyesini, \195\182zelliklerini art\196\177r\196\177r ve kahraman\196\177n pasif becerisi g\195\188\195\167lenir. T\195\188ketilen kahraman i\195\167in belirli miktarda Elit Ruh ve Kahraman Geli\197\159im Ta\197\159\196\177 iade edilir."}, {title = "Ekipman ve Gizemli Silah donat", content = "Kahramana ekipman giydirerek ya da gizemli silah donatarak kahraman\196\177n sava\197\159 g\195\188c\195\188 geli\197\159tirilebilir. Ekipman ve gizemli silah y\196\177ld\196\177z seviyesi ne kadar y\195\188ksek ise, kahraman sava\197\159 g\195\188c\195\188 o kadar y\195\188ksek olur."}, {title = "Kristal geli\197\159tir", content = "Seviye geli\197\159tirerek ve kahraman \195\188zerindeki kristali d\195\182n\195\188\197\159t\195\188rerek kahraman\196\177n becerileri geli\197\159tirilebilir. Geli\197\159tirme ve d\195\182n\195\188\197\159t\195\188rme i\197\159lemleri i\195\167in belirl