global.floatland_help = {string = "1.V\225\187\155i c\225\186\165p \196\145\225\187\153 c\225\187\167a ng\198\176\225\187\157i ch\198\161i \196\145\198\176\225\187\163c n\195\162ng l\195\170n \196\144\225\186\163o ch\195\173nh (c\225\186\165p \196\145\225\187\153 \196\144\225\186\163o ch\195\173nh t\198\176\198\161ng \196\145\198\176\198\161ng v\225\187\155i c\225\186\165p \196\145\225\187\153 Th\195\161p canh), nhi\225\187\129u \196\144\225\186\163o ng\225\186\171u nhi\195\170n s\225\186\189 xu\225\186\165t hi\225\187\135n quanh \196\145\225\186\163o ch\195\173nh.|||2. \196\144\225\186\163o ng\225\186\171u nhi\195\170n c\195\179 \196\144\225\186\163o qu\195\161i th\195\186 v\195\160 \196\144\225\186\163o ch\195\162u b\195\161u. \196\144\195\161nh b\225\186\161i qu\195\161i th\195\186 tr\195\170n \196\144\225\186\163o qu\195\161i th\195\186 v\195\160 \196\145\198\176\225\187\163c t\225\186\183ng th\198\176\225\187\159ng. Kh\195\161m ph\195\161 \196\144\225\186\163o ch\195\162u b\195\161u v\195\160 \196\145\198\176\225\187\163c t\225\186\183ng th\198\176\225\187\159ng.|||3. Ng\198\176\225\187\157i ch\198\161i c\195\179 th\225\187\131 s\225\187\173 d\225\187\165ng Hoa th\225\187\167y ti\195\170n t\195\173m \196\145\225\187\131 ch\225\187\145ng l\225\186\161i qu\195\161i th\195\186 m\225\187\153t l\225\186\167n. HP c\225\187\167a qu\195\161i th\195\186 s\225\186\189 \196\145\198\176\225\187\163c duy tr\195\172 n\225\186\191u ng\198\176\225\187\157i ch\198\161i kh\195\180ng th\225\187\131 \196\145\195\161nh b\225\186\161i n\195\179, ng\198\176\225\187\157i ch\198\161i c\195\179 th\225\187\131 ti\225\186\191p t\225\187\165c cu\225\187\153c chi\225\186\191n v\195\160o l\225\186\167n sau. C\225\187\169 m\225\187\151i 2 gi\225\187\157 ng\198\176\225\187\157i ch\198\161i s\225\186\189 nh\225\186\173n \196\145\198\176\225\187\163c 1 Hoa t\225\187\167y ti\195\170n t\195\173m, v\195\160 gi\225\186\163m d\225\186\167n khi m\225\187\169c Th\195\161p canh c\225\187\167a ng\198\176\225\187\157i ch\198\161i cao h\198\161n.|||4. \196\144\195\161nh b\225\186\161i qu\195\161i th\195\186 ho\225\186\183c m\225\187\159 r\198\176\198\161ng khi n\225\186\161p n\196\131ng l\198\176\225\187\163ng, n\225\186\191u kh\195\180ng c\195\161c h\195\178n \196\145\225\186\163o s\225\186\189 \196\145\198\176\225\187\163c n\225\186\161p n\196\131ng l\198\176\225\187\163ng sau khi \196\145\225\186\191m ng\198\176\225\187\163c.|||5. Nest of Void l\195\160 m\225\187\153t h\195\178n \196\145\225\186\163o b\225\186\165t bi\225\186\191n, ng\198\176\225\187\157i ch\198\161i c\195\179 th\225\187\131 kh\195\161m ph\195\161 n\195\179 \196\145\225\187\131 \196\145\225\186\165u l\225\186\161i BOSS ng\225\186\171u nhi\195\170n. Ng\198\176\225\187\157i ch\198\161i c\195\179 th\225\187\131 \196\145\198\176\225\187\163c t\225\186\183ng qu\195\160 ng\225\186\171u nhi\195\170n trong m\225\187\151i tr\225\186\173n \196\145\225\186\165u v\195\160 nhi\225\187\129u qu\195\160 t\225\186\183ng sau khi \196\145\195\161nh b\225\186\161i BOSS. BOSS c\197\169ng c\195\179 th\225\187\157i gian n\225\186\161p n\196\131ng l\198\176\225\187\163ng.|||6. Ng\198\176\225\187\157i ch\198\161i c\195\179 th\225\187\131 nh\225\186\173n \196\144\195\161 Void \225\187\159 \196\144\225\186\163o qu\195\161i th\195\186, \196\144\225\186\163o ch\195\162u b\195\161u v\195\160 Nest of Void. C\195\179 th\225\187\131 s\225\187\173 d\225\187\165ng \196\144\195\161 Void \196\145\225\187\131 x\195\162y nh\195\160 cho ng\198\176\225\187\157i ch\198\161i.|||7. Ng\198\176\225\187\157i ch\198\161i c\195\179 th\225\187\131 m\225\187\157i b\225\186\161n b\195\168 \196\145\225\186\191n gi\195\186p trong c\195\161c tr\225\186\173n \196\145\225\186\165u. C\225\187\169 m\225\187\151i 12 gi\225\187\157 ng\198\176\225\187\157i ch\198\161i c\195\179 th\225\187\131 nh\225\186\173n \196\145\198\176\225\187\163c 1 \196\145i\225\187\131m khi M\225\187\157i b\225\186\161n.|||8. Khi m\225\187\169c Th\195\161p canh \196\145\225\186\161t \196\145\225\186\191n 11 ho\225\186\183c cao h\198\161n, ng\198\176\225\187\157i ch\198\161i c\195\179 c\198\161 h\225\187\153i nh\225\186\173n \196\145\198\176\225\187\163c c\195\161c m\225\186\163nh v\225\187\143 Universal sau khi \196\145\195\161nh b\225\186\161i BOSS.|||9. Khi m\225\187\169c Th\195\161p canh t\196\131ng, qu\195\161i th\195\186 s\225\186\189 \196\145\198\176\225\187\163c t\196\131ng s\225\187\169c l\225\187\177c."}
global.airisland_help = {string = "1. Ng\198\176\225\187\157i ch\198\161i c\195\179 th\225\187\131 d\225\187\177ng c\195\161c B\195\163i m\195\172n v\195\160 Th\195\161nh t\195\173ch trong Nh\195\160 c\225\187\167a m\195\172nh. Ch\225\187\137 c\195\179 th\225\187\131 d\225\187\177ng c\195\161c B\195\163i m\195\172n trong H\225\187\145, ch\225\187\137 c\195\179 th\225\187\131 x\195\162y Th\195\161nh t\195\173ch tr\195\170n V\195\185ng \196\145\225\186\165t thi\195\170ng. S\225\187\145 l\198\176\225\187\163ng B\195\163i m\195\172n v\195\160 Th\195\161nh t\195\173ch li\195\170n quan \196\145\225\186\191n m\225\187\169c Th\195\161p canh.|||2. B\195\163i m\195\172n c\195\179 M\225\187\143 v\195\160ng, M\225\187\143 \196\145\195\161 qu\195\189 v\195\160 m\225\187\143 B\225\187\165i ma thu\225\186\173t. S\225\186\163n l\198\176\225\187\163ng cho ra t\225\187\171 \196\145\195\179 l\195\160 V\195\160ng, \196\144\195\161 qu\195\189 v\195\160 B\225\187\165i ma thu\225\186\173t. C\195\179 th\225\187\131 n\195\162ng c\225\186\165p c\195\161c m\225\187\143 n\195\160y \196\145\225\187\131 t\196\131ng s\225\186\163n l\198\176\225\187\163ng. M\225\187\151i lo\225\186\161i ng\198\176\225\187\157i ch\198\161i c\195\179 th\225\187\131 c\195\179 t\225\187\145i \196\145a 2 m\225\187\143.|||3. Ng\198\176\225\187\157i ch\198\161i ch\225\187\137 c\195\179 th\225\187\131 c\195\179 1 Th\195\161nh t\195\173ch thu ho\225\186\161ch v\195\160 Th\195\161nh t\195\173ch s\225\187\169c s\225\187\145ng, 2 \196\145\225\187\145i v\225\187\155i c\195\161c lo\225\186\161i Th\195\161nh t\195\173ch kh\195\161c. C\195\179 th\225\187\131 n\195\162ng c\225\186\165p Th\195\161nh t\195\173ch. Nh\225\187\175ng thu\225\187\153c t\195\173nh kh\195\161c m\195\160 c\195\161c Th\195\161nh t\195\173ch \196\145em l\225\186\161i ch\225\187\137 \195\161p d\225\187\165ng \196\145\225\187\145i v\225\187\155i tr\225\186\173n \196\145\195\161nh tr\195\170n \196\144\225\186\163o Thi\195\170n \196\145\195\160ng.|||4. Th\195\161nh t\195\173ch c\195\179 Th\195\161nh t\195\173ch thu ho\225\186\161ch, Th\195\161nh t\195\173ch s\225\187\169c s\225\187\145ng, Th\195\161nh t\195\173ch nhanh l\225\186\185, Th\195\161nh t\195\173ch b\225\186\161o ch\195\186a v\195\160 Th\195\161nh t\195\173ch v\225\186\167ng tr\196\131ng \196\145\225\187\143.|||5. Th\195\161nh t\195\173ch thu ho\225\186\161ch c\195\179 th\225\187\131 t\196\131ng s\225\186\163n l\198\176\225\187\163ng m\225\187\143, Th\195\161nh t\195\173ch s\225\187\169c s\225\187\145ng c\195\179 th\225\187\131 t\225\186\161o ra Hoa th\225\187\167y ti\195\170n t\195\173m, Th\195\161nh t\195\173ch v\225\186\167ng tr\196\131ng \196\145\225\187\143 c\195\179 th\225\187\131 t\196\131ng HP c\225\187\167a c\195\161c anh h\195\185ng, Th\195\161nh t\195\173ch b\225\186\161o ch\195\186a c\195\179 th\225\187\131 t\196\131ng s\225\187\169c T\225\186\165n c\195\180ng c\225\187\167a c\195\161c anh h\195\185ng v\195\160 Th\195\161nh t\195\173ch nhanh l\225\186\185 c\195\179 th\225\187\131 t\196\131ng T\225\187\145c \196\145\225\187\153 c\225\187\167a c\195\161c anh h\195\185ng."}
